mapper.select:
SELECT contract_num, oa_rlzyb_employee.emp_id AS emp_id,emp_name, oa_rlzyb_employee.emp_department AS emp_department,depart_name, oa_rlzyb_employee.emp_position AS emp_position,position_name, contract_id,contract_status,contract_belong_company,contract_startDate,contract_endDate,contract_delectStatus FROM oa_rlzyb_employee LEFT JOIN oa_rlzyb_department ON oa_rlzyb_employee.emp_department = oa_rlzyb_department.depart_id LEFT JOIN oa_rlzyb_position ON oa_rlzyb_employee.emp_position = oa_rlzyb_position.position_id LEFT JOIN oa_rlzyb_contract ON oa_rlzyb_employee.emp_id = oa_rlzyb_contract.emp_id WHERE (oa_rlzyb_employee.emp_id NOT IN ( SELECT oa_rlzyb_contract.emp_id FROM oa_rlzyb_contract ) OR contract_num IN (SELECT MAX(contract_num) FROM tyyhoa.oa_rlzyb_employee LEFT JOIN oa_rlzyb_contract ON oa_rlzyb_employee.emp_id = oa_rlzyb_contract.emp_id GROUP BY oa_rlzyb_employee.emp_id) )
and emp_name = #{emp_name}
and contract_id = #{contract_id}
and contract_startDate = #{contract_startDate}
and contract_endDate = #{contract_endDate}
and contract_status = #{contract_status}
and contract_status = #{contract_status}
and contract_status = #{contract_status}
and oa_rlzyb_employee.emp_id NOT IN ( SELECT oa_rlzyb_contract.emp_id FROM oa_rlzyb_contract)
ORDER BY contract_num DESC limit #{startPos},#{PageSize}

