mapper.update:
    UPDATE tyyhoa.oa_rlzyb_leave 
	SET
	leave_backTime = #{leave_backTime}
	WHERE
	leave_id = #{leave_id}

mapper.select:
    SELECT 
COUNT(1)
FROM oa_rlzyb_employee e
INNER JOIN oa_rlzyb_department  d ON e.emp_department = d.depart_id
INNER JOIN oa_rlzyb_position  p ON e.emp_position = p.position_id
INNER JOIN oa_rlzyb_leave l ON l.emp_id=e.emp_id

 
and depart_id = #{depart_id}

 
and emp_name = #{leave_approver}

 
and leave_type = #{leave_type}

 
and leave_startDate >= #{leave_startDate}
 
 
and leave_endDate <= #{leave_endDate}

 
and leave_delectStatus = 1

select l.emp_id, leave_id, emp_name, position_name, depart_name, leave_type, leave_reason, leave_approver, leave_delectStatus, leave_startDate, leave_endDate, leave_backTime, l.create_time, depart_id FROM oa_rlzyb_employee e INNER JOIN oa_rlzyb_department d ON e.emp_department = d.depart_id INNER JOIN oa_rlzyb_position p ON e.emp_position = p.position_id INNER JOIN oa_rlzyb_leave l ON l.emp_id=e.emp_id where leave_delectStatus = 1
and depart_id = #{depart_id}
and emp_name like concat('%',#{leave_approver},'%')
and leave_type = #{leave_type}
and leave_startDate >= #{leave_startDate}
and leave_endDate <= #{leave_endDate}
order by leave_id desc limit #{fromIndex},#{pageSize}
