mapper.select:
SELECT transfer_position_id, oa_rlzyb_transfer_position.emp_id, emp_name, transfer_position_reason, transfer_position_details, oa_rlzyb_position.position_id, oa_rlzyb_position.position_name, oa_rlzyb_department.depart_id, oa_rlzyb_department.depart_name, transfer_position_time, oa_rlzyb_transfer_position.create_time FROM oa_rlzyb_transfer_position INNER JOIN oa_rlzyb_employee ON oa_rlzyb_transfer_position.emp_id = oa_rlzyb_employee.emp_id INNER JOIN oa_rlzyb_department ON oa_rlzyb_transfer_position.depart_id = oa_rlzyb_department.depart_id INNER JOIN oa_rlzyb_position ON oa_rlzyb_transfer_position.position_id = oa_rlzyb_position.position_id where oa_rlzyb_transfer_position.transfer_position_delectStatus = 1
and emp_name like CONCAT("%",#{emp_name},"%")
ORDER BY transfer_position_id DESC limit #{startPos},#{PageSize}

SELECT oa_rlzyb_transfer_position.emp_id,emp_name,POSITION,position_name,depart,depart_name FROM oa_rlzyb_transfer_position INNER JOIN oa_rlzyb_employee ON oa_rlzyb_transfer_position.emp_id = oa_rlzyb_employee.emp_id INNER JOIN oa_rlzyb_position ON oa_rlzyb_transfer_position.position = oa_rlzyb_position.position_id INNER JOIN oa_rlzyb_department ON oa_rlzyb_transfer_position.depart = oa_rlzyb_department.depart_id where oa_rlzyb_transfer_position.transfer_position_delectStatus = 1
and emp_name like CONCAT("%",#{emp_name},"%")
ORDER BY transfer_position_id DESC limit #{startPos},#{PageSize}
